WindowManager::the().for_each_window_in_modal_chain(*this, [&](auto& window) {
        if (is_descendant_of(window))
            return IterationDecision::Continue;
        if (window.is_blocking() && this != &window)
            return IterationDecision::Break;
        return IterationDecision::Continue;
    })

